theorem snowman_volume :
  let sphereVolume (r : ℝ) := (4/3) * Real.pi * r^3
  let r₁ := (4 : ℝ)
  let r₂ := (6 : ℝ)
  let r₃ := (3 : ℝ)
  let r₄ := (7 : ℝ)
  sphereVolume r₁ + sphereVolume r₂ + sphereVolume r₃ + sphereVolume r₄ = (2600/3) * Real.pi:= by

theorem domain_intersection_theorem :
    let M := {x : ℝ | -2 < x ∧ x < 4}
    let N := {x : ℝ | x < 1 ∨ x ≥ 3}
