theorem circle_line_distance :
  let circle_center : ℝ × ℝ := (1, 0)
  let line_coeff : ℝ × ℝ × ℝ := (2, -1, 1)
  let dist := |line_coeff.1 * circle_center.1 + line_coeff.2.1 * circle_center.2 + line_coeff.2.2| /
              Real.sqrt (line_coeff.1 ^ 2 + line_coeff.2.1 ^ 2)
  dist = 3 * Real.sqrt 5 / 5:= by

