theorem min_distance_circle_to_line :
  let C : Set (ℝ × ℝ) := {p | (p.1 - 2)^2 + (p.2 - 2)^2 = 2}
  let l : Set (ℝ × ℝ) := {p | p.1 + p.2 = 1}
  let d (p : ℝ × ℝ) := |p.1 + p.2 - 1| / Real.sqrt 2
  ∀ p ∈ C, d p ≥ Real.sqrt 2 / 2 ∧
  ∃ q ∈ C, d q = Real.sqrt 2 / 2:= by

